import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import contactImg from "../assets/img/avatar-face1.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';
import dotenv from 'dotenv';
dotenv.config();

export const Contact = () => {
  const formInitialDetails = {
    Nom: '',
    Prénom: '',
    email: '',
    tel: '',
    message: ''
  }
  const [formDetails, setFormDetails] = useState(formInitialDetails);
  const [buttonText, setButtonText] = useState('Envoyer');
  const [status, setStatus] = useState({});

  const onFormUpdate = (category, value) => {
      setFormDetails({
        ...formDetails,
        [category]: value
      })
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setButtonText("Envoi en cours...");
    try {
      const response = await fetch("http://localhost:3000/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify(formDetails),
      });
      setButtonText("Envoyer");
      const result = await response.json();
      setFormDetails(formInitialDetails);
      if (result.code == 200) {
        setStatus({ success: true, message: 'Votre message a été envoyé avec succès !'});
        sendEmail();
      } else {
        setStatus({ success: false, message: 'Oups une erreur est survenue'});
      }
    } catch (error) {
      console.log(error);
      setStatus({ success: false, message: 'Oups une erreur est survenue'});
    }
  };

  const sendEmail = () => {
    window.emailjs.send(
      process.env.SERVICE_ID,
      process.env.TEMPLATE_ID,
      {
        from_name: formDetails.Prénom + ' ' + formDetails.Nom,
        reply_to: formDetails.email,
        message: formDetails.message,
        tel: formDetails.tel
      },
      process.env.USER_ID
    ).then((result) => {
        console.log(result.text);
      }, (error) => {
        console.log(error.text);
      }
    );
  }
  return (
    <section className="contact" id="connect">
      <Container>
        <Row className="align-items-center">
          <Col size={12} md={6}>
            <TrackVisibility>
              {({ isVisible }) =>
                <img className={isVisible ? "animate__animated animate__zoomIn" : ""} src={contactImg} alt="Contact Us"/>
              }
            </TrackVisibility>
          </Col>
          <Col size={12} md={6}>
            <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                <h2>Me contacter</h2>
                <form onSubmit={handleSubmit}>
                  <Row>
                    <Col size={12} sm={6} className="px-1">
                      <input type="text" value={formDetails.firstName} placeholder="Nom" onChange={(e) => onFormUpdate('Nom', e.target.value)} />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input type="text" value={formDetails.lasttName} placeholder="Prénom" onChange={(e) => onFormUpdate('Prenom', e.target.value)}/>
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input type="email" value={formDetails.email} placeholder="Adresse mail" onChange={(e) => onFormUpdate('email', e.target.value)} />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input type="tel" value={formDetails.phone} placeholder="Numéro de téléphone" onChange={(e) => onFormUpdate('tel', e.target.value)}/>
                    </Col>
                    <Col size={12} className="px-1">
                      <textarea rows="6" value={formDetails.message} placeholder="Message" onChange={(e) => onFormUpdate('message', e.target.value)}></textarea>
                      <button type="submit"><span>{buttonText}</span></button>
                    </Col>
                    {
                      status.message &&
                      <Col>
                        <p className={status.success === false ? "danger" : "success"}>{status.message}</p>
                      </Col>
                    }
                  </Row>
                </form>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  )
}
